mapper.update:
update user
phone_number=#{phoneNumber}, password=#{password}, id_number=#{idNumber}, name=#{name}, description=#{description}, gender=#{gender}, age=#{age}, address=#{address}, wechat=#{weChat}, qq=#{qq}, role_type=#{roleType}, account_status=#{accountStatus}, comment_status=#{commentStatus}, create_time=#{createTime}, update_time=#{updateTime},
where user_id=#{userId}

mapper.select:
select * from user

    and user_id=#{userId}
    and phone_number like concat('%',#{phoneNumber},'%')
    and name like concat('%',#{name},'%')
    and id_number like concat('%',#{idNumber},'%')
    and role_type=#{roleType}
    and account_status=#{accountStatus}

order by role_type desc, user_id asc
